import React, { useState } from 'react';
import Button from '../../UI/Button';

export default function Notes({ onClose }) {
  const [notesTitle, setNotesTitle] = useState('');
  const [notesDescription, setNotesDescription] = useState('');

  const handleSubmit = (e) => {
    e.preventDefault();
    if (!notesTitle || !notesDescription) {
      alert("Please fill both fields!");
      return;
    }

    console.log("Note Added:", { title: notesTitle, description: notesDescription });

    // Clear input fields after submit
    setNotesTitle('');
    setNotesDescription('');
    onClose(); // Close modal after adding
  };

  return (
    <form className="note-form" onSubmit={handleSubmit}>
      <div className="form-control">
        <label htmlFor="noteTitle">Note Title</label>
        <input
          type="text"
          id="noteTitle"
          value={notesTitle}
          onChange={(e) => setNotesTitle(e.target.value)}
          placeholder="Enter note title"
        />
      </div>

      <div className="form-control">
        <label htmlFor="noteDescription">Note Description</label>
        <textarea
          id="noteDescription"
          value={notesDescription}
          onChange={(e) => setNotesDescription(e.target.value)}
          placeholder="Enter note description"
        />
      </div>

      <div className="btn-group">
        <Button type="submit">Add to Note</Button>
        <Button type="button" onClick={onClose}>Close</Button>
      </div>
    </form>
  );
}
